package handler;

import com.amazonaws.services.lambda.runtime.events.APIGatewayProxyRequestEvent;
import models.RedactedDocument;
import models.UserCredentials;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;
import services.NotificationService;
import services.PersistenceService;
import services.SsnRedactionService;
import services.UserCredentialsService;

import java.awt.image.BufferedImage;
import java.util.ArrayList;
import java.util.List;
import java.util.Map;

import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.*;

@RunWith(MockitoJUnitRunner.class)
public class ImageUploadHandlerTest {

    @Mock
    private UserCredentialsService userCredentialsService;
    @Mock
    private SsnRedactionService ssnRedactionService;
    @Mock
    private PersistenceService persistenceService;
    @Mock
    private NotificationService notificationService;
    @InjectMocks
    private ImageUploadHandler imageUploadHandler;

    @Test
    public void handleRequest_GivenOneOrMoreSsn_SendNotification() {
        // given
        APIGatewayProxyRequestEvent request = new APIGatewayProxyRequestEvent()
                .withHeaders(Map.of("Authorization", "eyJraWQiOiIzS2t4ZkZmZjU0XC9cLzFcL0VURDBxRmNEM1NUNUxSZDEzRUl5S0lRUCt5Um5NPSIsImFsZyI6IlJTMjU2In0..gdYMTCOdxF_CUIZznkyNRiuadfTaLtmI0KfKua8YfGDrWaEakH70zPd_BgHL1Q2oG8waKN4PemvCHhhoRJNdyjNLACEqwVT9Vx7ACdEnwqBCHvafbX1gsWWequu6JDCGt0OwvpCtq7P_MeTth-FWzRSA5hZL5ocXW5YHybLulxS8jI89X1vqGF-5tmB37WMOKL1VAQbiOvM-lfB_vhNjP2TYxjwT67VgMOWyZsYAZIzw0wuMW_46W0DotHwt_ftnqoNU_GRFwEe0DTkRuEqsqvfdeiRkeVtaqimxEMbOl3kh4lXS8CFAj_8G-BOWFyZF8aef12luh9HXZIDWVfEmCg"))
                .withBody(getBase64EncodedString());
        RedactedDocument redactedDocument = new RedactedDocument(
                new BufferedImage(1, 1, 1),
                "this is text",
                List.of("list", "of", "items"),
                "image/png"
        ).withUserCredentials(new UserCredentials("zach", "+15555555555"));

        // when
        when(ssnRedactionService.redact(any())).thenReturn(redactedDocument);

        imageUploadHandler.handleRequest(request, any());

        // then
        verify(userCredentialsService, times(1)).getUserCredentials(anyString());
        verify(ssnRedactionService, times(1)).redact(any());
        verify(persistenceService, times(1)).save(redactedDocument);
        verify(notificationService, times(1)).sendNotifications(redactedDocument);
        verifyNoMoreInteractions(ssnRedactionService, persistenceService, notificationService);
    }

    @Test
    public void handleRequest_GivenNoSsnIsFound_DoNotSendNotification() {
        // given
        APIGatewayProxyRequestEvent request = new APIGatewayProxyRequestEvent()
                .withHeaders(Map.of("Authorization", "eyJraWQiOiIzS2t4ZkZmZjU0XC9cLzFcL0VURDBxRmNEM1NUNUxSZDEzRUl5S0lRUCt5Um5NPSIsImFsZyI6IlJTMjU2In0..gdYMTCOdxF_CUIZznkyNRiuadfTaLtmI0KfKua8YfGDrWaEakH70zPd_BgHL1Q2oG8waKN4PemvCHhhoRJNdyjNLACEqwVT9Vx7ACdEnwqBCHvafbX1gsWWequu6JDCGt0OwvpCtq7P_MeTth-FWzRSA5hZL5ocXW5YHybLulxS8jI89X1vqGF-5tmB37WMOKL1VAQbiOvM-lfB_vhNjP2TYxjwT67VgMOWyZsYAZIzw0wuMW_46W0DotHwt_ftnqoNU_GRFwEe0DTkRuEqsqvfdeiRkeVtaqimxEMbOl3kh4lXS8CFAj_8G-BOWFyZF8aef12luh9HXZIDWVfEmCg"))
                .withBody(getBase64EncodedString());
        RedactedDocument redactedDocument = new RedactedDocument(
                new BufferedImage(1, 1, 1),
                "this is text",
                new ArrayList<>(),
                "image/png"
        ).withUserCredentials(new UserCredentials("zach", "+15555555555"));

        // when
        when(ssnRedactionService.redact(any())).thenReturn(redactedDocument);

        imageUploadHandler.handleRequest(request, any());

        // then
        verify(userCredentialsService, times(1)).getUserCredentials(anyString());
        verify(ssnRedactionService, times(1)).redact(any());
        verify(persistenceService, times(1)).save(redactedDocument);
        verifyNoInteractions(notificationService);
        verifyNoMoreInteractions(ssnRedactionService, persistenceService);
    }

    private String getBase64EncodedString() {
        return "data:image/jpeg;base64,";
    }
}
